<script>
import { db } from "@/Firebase.js";
import {
    doc,
    getDoc,
    getDocs,
    query,
    collection,
    updateDoc,
    where,
} from "firebase/firestore";
import Chart from "chart.js/auto";
import jsPDF from "jspdf";
import "jspdf-autotable";
import Amiri_Regular from "@/assets/fonts/Amiri-Regular.js";
import { mapActions } from "pinia";
import { usenotification } from "../store/notification.js";

export default {
    data() {
        return {
            exams: [],
            studySchedules: [],
            educationalContent: [],
            photos: [],
            loading1: false,
            isAuthenticated: false,
            loading: true, // أضفناه لتفعيل عنصر التحميل
            CreateChart: null,
            interval: null,
            value: 0,
            results: [],
            paymentMethod: null,
            dialogSuccess: false,
            transitionTimeline: false, // متغير للتحكم في الانتقال للـ timeline-item-content
            transitionProgress: false, // متغير للتحكم في الانتقال للبروجريس بار
            alertMessage: false,
            reachedProgress: false,
            currentActive: 0,
            // circles: [],
            totalAmount: null,
            paidAmount: 0,
            progress: 0,
            tab_5: null,
            tab_4: null,
            tab_1: 0,
            tab_2: null,
            tab_3: null,
            tab: "option-1", // تحديد التاب الافتراضي
            selectedGrade: null,
            gradeLevels: ["الصف الأول", "الصف الثاني", "الصف الثالث"],
            selectedMonth: "شهر أكتوبر",
            selectedPlan: null,
            selectPaid: ["شهر", "شهرين", "3 شهور", "4 شهور", "5 شهور"],
            amount: 0,
            selectedPaymentPlan: null,
            paymentPlans: ["شهر", "شهرين", "3 شهور", "4 شهور", "5 شهور"],
            steps: [
                "بدأ ",
                "الخطوة 1",
                "الخطوة 2",
                "الخطوة 3",
                "الخطوة 4",
                "الخطوة 5",
            ],

            // خاص بالبروحريس بار وتقسيم القسط

            student: {
                notifications: [],
                student_name: "",
                National_id: "",
                educational_level: "",
                class: "",
                section: "",
                gender: "",
                birthday: "",
                phone: "",
                tests: [],
                // Results: [], // بيانات النتائج ستكون هنا
                state: true, // إضافة حالة للتحكم في عرض الاختبارات
                name: "أحمد محمد",
                age: 12,
                BithOfDate: "2009-08-05",
                gradeLevel: "الصف الخامس",
                schoolYear: "2023-2024",
                photo: {
                    url1: "https://cdn.vuetifyjs.com/images/cards/sunshine.jpg",
                },
            },
        };
    },
    mounted() {
        this.get_notifications("students_notification");
        this.interval = setInterval(() => {
            if (this.value === 100) {
                clearInterval(this.interval);
                return;
            }
            this.value += 10;
        }, 100);
    },

    async created() {
        const documentId = this.$route.params.id; // استلام documentId من الـ route params
        this.loading = true; // بدء حالة التحميل

        try {
            // محاولة الحصول على بيانات الطالب
            const docSnap = await getDoc(doc(db, "students", documentId));
            if (docSnap.exists()) {
                const studentData = docSnap.data();
                this.student = studentData;
                this.results = studentData.Results || []; // استخراج بيانات Results
                this.isAuthenticated = true;

                // جلب بيانات الإشعارات من حقل Notifications
                this.notifications = studentData.Notifications || [];
                this.photos = studentData.photos || [];
                this.exams = studentData.exams || [];

                // جلب بيانات الجدول الدراسي بناءً على بيانات الطالب
                const studyScheduleQuery = query(
                    collection(db, "studySchedule"),
                    where("class", "==", studentData.class),
                    where(
                        "educational_level",
                        "==",
                        studentData.educational_level
                    ),
                    where("section", "==", studentData.section)
                );

                const scheduleSnapshot = await getDocs(studyScheduleQuery);
                if (!scheduleSnapshot.empty) {
                    this.studySchedules = scheduleSnapshot.docs.map((doc) =>
                        doc.data()
                    );
                } else {
                    this.studySchedules = [];
                }

                // جلب بيانات Educationalcontent بناءً على بيانات الطالب
                const educationalContentQuery = query(
                    collection(db, "Educationalcontent"),
                    where("classId", "==", studentData.class),
                    where(
                        "educational_level",
                        "==",
                        studentData.educational_level
                    ),
                    where("sectionId", "==", studentData.section)
                );

                const educationalContentSnapshot = await getDocs(
                    educationalContentQuery
                );
                if (!educationalContentSnapshot.empty) {
                    this.educationalContent =
                        educationalContentSnapshot.docs.map((doc) =>
                            doc.data()
                        );

                    // تحديث بيانات الطالب بالمحتوى التعليمي الجديد
                    await updateDoc(doc(db, "students", documentId), {
                        educationalContent: this.educationalContent,
                    });
                } else {
                    this.educationalContent = [];
                }
            } else {
                this.isAuthenticated = false;
            }
        } catch (error) {
            console.error("Error fetching data:", error);
            this.isAuthenticated = false;
        } finally {
            this.loading = false; // إنهاء حالة التحميل
        }
    },

    beforeUnmount() {
        clearInterval(this.interval);
    },
    computed: {
        filteredResults() {
            if (!this.results || !this.selectedMonth) return []; // التأكد من أن results و selectedMonth مُهيّأت
            return this.results.flatMap((result) =>
                result.Monthly
                    ? result.Monthly.filter(
                          (monthly) =>
                              monthly.Certificate_title === this.selectedMonth
                      )
                    : []
            );
        },
        filteredPhotos() {
            if (!this.selectedGrade) {
                return this.student.photos;
            }
            return this.student.photos.filter(
                (photo) => photo.grade === this.selectedGrade
            );
        },
        selectedMonthlyDegrees() {
            // تأكد من أن student و Results و Monthly ليست null أو undefined
            if (
                this.student &&
                this.student.Results &&
                Array.isArray(this.student.Results) &&
                this.student.Results.length > 0
            ) {
                const selectedResult = this.student.Results.find(
                    (result) =>
                        result.Monthly &&
                        result.Monthly.length > 0 &&
                        result.Monthly.find(
                            (month) =>
                                month.Certificate_title === this.selectedMonth
                        )
                );

                if (selectedResult) {
                    const selectedMonth = selectedResult.Monthly.find(
                        (month) =>
                            month.Certificate_title === this.selectedMonth
                    );
                    return selectedMonth ? selectedMonth.Degrees : [];
                }
            }
            return [];
        },
        numberOfMonths() {
            if (!this.selectedPlan) return [];
            const monthsMap = {
                شهر: 1,
                شهرين: 2,
                "3 شهور": 3,
                "4 شهور": 4,
                "5 شهور": 5,
            };
            return Array.from(
                { length: monthsMap[this.selectedPlan] },
                (_, i) => i + 1
            );
        },
        installmentAmount() {
            if (!this.selectedPlan) return 0;
            const monthsMap = {
                شهر: 1,
                شهرين: 2,
                "3 شهور": 3,
                "4 شهور": 4,
                "5 شهور": 5,
            };
            return Math.floor(this.totalAmount / monthsMap[this.selectedPlan]);
        },
        remainingAmount() {
            return Math.max(this.totalAmount - this.paidAmount, 0);
        },
    },
    methods: {
        ...mapActions(usenotification, ["get_notifications"]),
        async loadStudent(id) {
            try {
                const studentRef = doc(db, "students", id);
                const studentSnapshot = await getDocs(studentRef);
                if (studentSnapshot.exists()) {
                    this.student = studentSnapshot.data();
                } else {
                    console.error("No such student!");
                    this.student = null;
                }
            } catch (error) {
                console.error("Error loading student:", error);
                this.student = null;
            }
        },
        getNotificationColor(type) {
            switch (type) {
                case "تنبيه":
                    return "blue lighten-3";
                case "سئ":
                    return "orange lighten-3";
                case "error":
                    return "red lighten-3";
                case "جيد":
                    return "green lighten-3";
                default:
                    return "blue lighten-3";
            }
        },
        getNotificationIcon(type) {
            switch (type) {
                case "تنبيه":
                    return "mdi-information";
                case "سئ":
                    return "mdi-alert";
                case "error":
                    return "mdi-alert-circle";
                case "جيد":
                    return "mdi-check-circle";
                default:
                    return "mdi-information";
            }
        },
        createChart(data) {
            const ctx = document.getElementById("myChart");
            if (ctx) {
                // تحقق مما إذا كان هناك مخطط موجود وقم بتدميره
                if (this.myChart) {
                    this.myChart.destroy();
                }

                console.log("start createChart");
                this.CreateChart = true;
                this.myChart = new Chart(ctx, {
                    type: "doughnut",
                    data: {
                        datasets: [
                            {
                                label: "المصروفات",
                                data: data,
                                backgroundColor: ["#336699", "#d8588c"],
                                hoverOffset: 4,
                            },
                        ],
                    },
                });
            } else {
                console.log("error");
            }
        },

        downloadPDF() {
            const doc = new jsPDF("landscape");
            doc.addFileToVFS("Amiri-Regular.ttf", Amiri_Regular);
            doc.addFont("Amiri-Regular.ttf", "Amiri-Regular", "normal");
            doc.setFont("Amiri-Regular");
            //border
            const pageWidth = doc.internal.pageSize.getWidth();
            const pageHeight = doc.internal.pageSize.getHeight();
            const margin = 5; // حجم الهامش من كل جانب
            doc.setLineWidth(0.5);
            doc.rect(
                margin,
                margin,
                pageWidth - 2 * margin,
                pageHeight - 2 * margin
            );
            // Profile Certification

            // إضافة الصورة
            const imgData =
                "data:image/jpeg;base64,"; // حط النص اللي جبته من الموقع هنا
            doc.addImage(imgData, "JPEG", 15, 15, 50, 50);

            // إضافة النص تحت الصورة
            // دى فنكشن built in
            doc.setFontSize(14);
            doc.text("معهد السراج المنير الأزهرى", 16, 70);

            doc.text("الاسم:  " + this.student.student_name, 248, 30);
            doc.text(
                "المرحله الدراسيه:  " + this.student.educational_level,
                195,
                40
            );
            doc.text("السنه الدراسيه:  " + this.student.year, 235, 50);
            doc.text(this.selectedMonth, 255, 60);

            doc.setFontSize(30);
            doc.text("شهادة", 130, 84);
            // ******************************************************
            const tableColumn = [
                "الدرجة النهائية للطالب",
                "الدرجة الكبرى",
                "الدرجة الصغرى",
                "تقييم السلوك",
                "اسم المعلم",
                "اسم المادة",
            ];
            const tableRows = [];
            this.selectedMonthlyDegrees.forEach((degree) => {
                const degreeData = [
                    degree.Minor_degree,
                    degree.Student_degree,
                    degree.Major_degree,
                    degree.Behavior_assessment,
                    degree.Teacher_Name,
                    degree.Subject_Name,
                ];
                tableRows.push(degreeData);
            });
            //  ده التيبل
            doc.autoTable({
                head: [tableColumn],
                body: tableRows,
                startY: 100,
                styles: {
                    font: "Amiri-Regular",
                    fontSize: 10,
                    cellWidth: "wrap",
                    halign: "right", // تحديد محاذاة النص إلى اليمين
                    valign: "middle", // تحديد محاذاة عمودياً
                    cellPadding: 5, // تحديد هامش الخلية
                },
                columnStyles: {
                    0: { cellWidth: "auto" },
                    1: { cellWidth: "auto" },
                    2: { cellWidth: "auto" },
                    3: { cellWidth: "auto" },
                    4: { cellWidth: "auto" },
                    5: { cellWidth: "auto" },
                },
            });

            doc.save(this.selectedMonth + ".pdf");
        },
        updatePaymentOptions() {
            if (this.paymentMethod === "نظام التقسيط") {
                this.selectedPlan = null;
                this.paidAmount = 0;
                this.amount = 0;
            }
        },
        payAmount() {
            const amountToPay = parseInt(this.amount);

            if (isNaN(amountToPay) || amountToPay <= 0) {
                this.showAlert("الرجاء إدخال المبلغ صحيحا");
                return;
            }

            this.paidAmount += amountToPay;
            // this.amount = 0;
            this.dialogSuccess = true;
        },
        validateTotalAmount() {
            if (this.totalAmount !== null) {
                if (isNaN(this.totalAmount) || this.totalAmount < 0) {
                    this.showAlert("لابد ان يكون رقما وليس سالبا");
                    this.totalAmount = null;
                }
            }
        },
        showAlert(message) {
            this.alertMessage = message;
            setTimeout(() => {
                this.alertMessage = "";
            }, 3000);
        },
        resetPayment() {
            this.paidAmount = 0;
        },
    },
    watch: {
        amount(newValue) {
            const amountToPay = parseInt(newValue);

            if (!isNaN(amountToPay) && amountToPay > 0) {
                this.paidAmount = amountToPay;
            } else {
                this.paidAmount = 0;
            }
        },
    },
};
</script>

